import { useEffect, useState } from 'react';
import './AboutUs.css';

function AboutUs() {
  const [info, setInfo] = useState(null);
  const [error, setError] = useState(null);

  // Simulación local de datos
  const fallbackInfo = {
    description: "Somos Tejelanas Vivi, un emprendimiento local de Laguna de Zapallar dedicado al arte del tejido. Ofrecemos lanas naturales, vellón y talleres comunitarios para fomentar la creatividad y el tejido colectivo.",
  };

  useEffect(() => {
    fetch('https://www.clinicatecnologica.cl/ipss/tejelanasVivi/api/v1/about-us/', {
      headers: {
        Authorization: 'Bearer ipss.get',
      },
    })
      .then(response => {
        if (!response.ok) throw new Error('Respuesta inválida');
        return response.json();
      })
      .then(data => setInfo(data))
      .catch(err => {
        console.warn('Fallo la API. Usando datos locales.', err.message);
        setError('Mostrando información local');
        setInfo(fallbackInfo);
      });
  }, []);

  return (
    <section className="about-us">
      <h2>¿Quiénes somos?</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {info ? <p>{info.description}</p> : <p>Cargando...</p>}
    </section>
  );
}

export default AboutUs;
